  Sub Main()

    ' Create new ExcelFile
    Dim ef = New ExcelFile()

    ' Add sheet
    Dim ws = ef.Worksheets.Add("Sheet1")

    ' Fill sheet
    For i As Integer = 0 To 5
      ws.Cells(i, 0).Value = i   ' Integer value
      ws.Cells(i, 1).Value = "Row: " + i.ToString() ' String value
    Next

